        count[0]++;
        countDelete(arr, 2 * idx, count);
        countDelete(arr, 2 * idx + 1, count);
    }

    private void visitTree() {
        if (tree == null) return;

        currentTraversalPath = new ArrayList<>();
        StringBuilder sb = new StringBuilder();

        if (inorderBtn.isSelected()) {
            java.util.List<Integer> inorderResult = tree.inorder();
            currentTraversalPath.addAll(inorderResult);
            sb.append("中序: ").append(inorderResult).append("\n");
        }
        if (preorderBtn.isSelected()) {
            java.util.List<Integer> preorderResult = tree.preorder();
            if (!inorderBtn.isSelected()) currentTraversalPath.addAll(preorderResult);
            sb.append("前序: ").append(preorderResult).append("\n");
        }
        if (postorderBtn.isSelected()) {
            java.util.List<Integer> postorderResult = tree.postorder();
            if (!inorderBtn.isSelected() && !preorderBtn.isSelected()) currentTraversalPath.addAll(postorderResult);
            sb.append("後序: ").append(postorderResult).append("\n");
        }

        if (sb.length() == 0) {
            sb.append("請至少選擇一種走訪方式");
            outputArea.setText(sb.toString());
            return;
        }

        // 設定走訪路徑到視覺化面板
        treePanel.setVisitPath(currentTraversalPath);
        outputArea.setText(sb.toString());

        // 啟用控制按鈕
        stepBtn.setEnabled(true);
        resetBtn.setEnabled(true);
    }

    private void stepTraversal() {
        if (currentTraversalPath != null && !currentTraversalPath.isEmpty()) {
            if (treePanel.hasMoreSteps()) {
                treePanel.showNextVisitStep();
                if (!treePanel.hasMoreSteps()) {
                    stepBtn.setEnabled(false);
                }
            }
        }
    }

    private void resetTraversal() {
        if (walkTimer.isRunning()) {
            walkTimer.stop();
        }
        treePanel.clearVisitPath();
        stepBtn.setEnabled(currentTraversalPath != null && !currentTraversalPath.isEmpty());
        resetBtn.setEnabled(false);
    }

    public static void main(String[] args) {
        SwingUtilities.invokeLater(() -> {
            BinaryTreeArrayGUI gui = new BinaryTreeArrayGUI();
            gui.setVisible(true);
        });
    }
}

import javax.swing.*;
import java.awt.*;
import java.awt.geom.Ellipse2D;
import java.awt.geom.Line2D;
import java.util.ArrayList;

public class TreeVisualizationPanel extends JPanel {
    private Integer[] treeArray;
    private java.util.List<Integer> visitPath;
    private int currentVisitIndex;
    private static final int NODE_RADIUS = 20;
    private static final int LEVEL_HEIGHT = 80;
    private static final int MIN_HORIZONTAL_SPACING = 50;

    public TreeVisualizationPanel() {
        setBackground(new Color(248, 249, 250));
        setPreferredSize(new Dimension(600, 400));
        visitPath = new ArrayList<>();
        currentVisitIndex = -1;
    }

    public void setTreeArray(Integer[] arr) {
        this.treeArray = arr;
        clearVisitPath();
        repaint();
    }

    public void setVisitPath(java.util.List<Integer> path) {
        this.visitPath = path;
        this.currentVisitIndex = -1;
        repaint();
    }

    public void showNextVisitStep() {
        if (currentVisitIndex < visitPath.size() - 1) {
            currentVisitIndex++;
            repaint();
        }
    }

    public void clearVisitPath() {
        this.visitPath.clear();
        this.currentVisitIndex = -1;
        repaint();
    }

    public boolean hasMoreSteps() {
        return currentVisitIndex < visitPath.size() - 1;
    }

    @Override
    protected void paintComponent(Graphics g) {
        super.paintComponent(g);
        if (treeArray == null || treeArray.length <= 1) return;

        Graphics2D g2d = (Graphics2D) g.create();
        g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
        g2d.setFont(new Font("Arial", Font.BOLD, 12));

        // 計算樹的最大層數
        int maxLevel = (int) (Math.log(treeArray.length - 1) / Math.log(2)) + 1;

        // 先畫連接線，再畫節點（避免線條覆蓋節點）
        drawConnections(g2d, maxLevel);
        drawNodes(g2d, maxLevel);

        g2d.dispose();
    }

    private void drawConnections(Graphics2D g2d, int maxLevel) {
        g2d.setColor(Color.BLACK);
        g2d.setStroke(new BasicStroke(2));

        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            int leftChild = 2 * i;
            int rightChild = 2 * i + 1;

            Point parentPos = getNodePosition(i, maxLevel);

            // 畫左子節點連線
            if (leftChild < treeArray.length && treeArray[leftChild] != null) {
                Point leftPos = getNodePosition(leftChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, leftPos.x, leftPos.y));
            }

            // 畫右子節點連線
            if (rightChild < treeArray.length && treeArray[rightChild] != null) {
                Point rightPos = getNodePosition(rightChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, rightPos.x, rightPos.y));
            }
        }
    }

    private void drawNodes(Graphics2D g2d, int maxLevel) {
        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            Point pos = getNodePosition(i, maxLevel);
            boolean isRoot = (i == 1);
            boolean isLeaf = isLeafNode(i);
            boolean isCurrentVisit = currentVisitIndex >= 0 && currentVisitIndex < visitPath.size()
                                   && visitPath.get(currentVisitIndex).equals(treeArray[i]);
            boolean isVisited = isNodeVisited(treeArray[i]);

            // 設定節點顏色
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 215, 0));  // 當前走訪節點：金黃色
            } else if (isVisited) {
                g2d.setColor(new Color(144, 238, 144));  // 已走訪節點：淡綠色
            } else if (isRoot) {
                g2d.setColor(Color.BLACK);  // 根節點黑色背景
            } else if (isLeaf) {
                g2d.setColor(Color.WHITE);  // 葉節點白色背景
            } else {
                g2d.setColor(new Color(200, 200, 200));  // 中間節點灰色背景
            }

            // 畫節點圓圈
            Ellipse2D circle = new Ellipse2D.Double(
                pos.x - NODE_RADIUS, pos.y - NODE_RADIUS,
                2 * NODE_RADIUS, 2 * NODE_RADIUS
            );
            g2d.fill(circle);

            // 畫節點邊框
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 140, 0));  // 當前節點橙色邊框
                g2d.setStroke(new BasicStroke(4));
            } else {
                g2d.setColor(Color.BLACK);
                g2d.setStroke(new BasicStroke(2));
            }
            g2d.draw(circle);

            // 設定文字顏色
            if (isRoot && !isCurrentVisit && !isVisited) {
                g2d.setColor(Color.WHITE);  // 根節點白色文字
            } else {
                g2d.setColor(Color.BLACK);  // 其他節點黑色文字
            }

            // 畫節點值
            String text = treeArray[i].toString();
            FontMetrics fm = g2d.getFontMetrics();
            int textWidth = fm.stringWidth(text);
            int textHeight = fm.getAscent();
            g2d.drawString(text,
                pos.x - textWidth / 2,
                pos.y + textHeight / 2 - 2);
        }
    }

    private boolean isNodeVisited(Integer value) {
        for (int i = 0; i <= currentVisitIndex && i < visitPath.size(); i++) {
            if (visitPath.get(i).equals(value)) {
                return true;
            }
        }
        return false;
    }

    private Point getNodePosition(int index, int maxLevel) {
        int level = (int) (Math.log(index) / Math.log(2));
        int nodesInLevel = 1 << level;
        int positionInLevel = index - (1 << level);

        int panelWidth = getWidth();
        int panelHeight = getHeight();

        // 計算水平位置
        int totalWidth = panelWidth - 2 * NODE_RADIUS;
        int spacing = Math.max(MIN_HORIZONTAL_SPACING, totalWidth / (nodesInLevel + 1));
        int x = NODE_RADIUS + spacing * (positionInLevel + 1);

        // 確保節點在面板範圍內
        if (nodesInLevel > 1) {
            x = NODE_RADIUS + (totalWidth * (positionInLevel + 1)) / (nodesInLevel + 1);
        } else {
            x = panelWidth / 2;
        }

        // 計算垂直位置
        int y = NODE_RADIUS + 30 + level * LEVEL_HEIGHT;

        return new Point(x, y);
    }

    private boolean isLeafNode(int index) {
        int leftChild = 2 * index;
        int rightChild = 2 * index + 1;

        boolean hasLeftChild = leftChild < treeArray.length && treeArray[leftChild] != null;
        boolean hasRightChild = rightChild < treeArray.length && treeArray[rightChild] != null;

        return !hasLeftChild && !hasRightChild;
    }
}

import javax.swing.*;
import java.awt.*;
import java.awt.geom.Ellipse2D;
import java.awt.geom.Line2D;
import java.util.ArrayList;

public class TreeVisualizationPanel extends JPanel {
    private Integer[] treeArray;
    private java.util.List<Integer> visitPath;
    private int currentVisitIndex;
    private static final int NODE_RADIUS = 20;
    private static final int LEVEL_HEIGHT = 80;
    private static final int MIN_HORIZONTAL_SPACING = 50;

    public TreeVisualizationPanel() {
        setBackground(new Color(248, 249, 250));
        setPreferredSize(new Dimension(600, 400));
        visitPath = new ArrayList<>();
        currentVisitIndex = -1;
    }

    public void setTreeArray(Integer[] arr) {
        this.treeArray = arr;
        clearVisitPath();
        repaint();
    }

    public void setVisitPath(java.util.List<Integer> path) {
        this.visitPath = path;
        this.currentVisitIndex = -1;
        repaint();
    }

    public void showNextVisitStep() {
        if (currentVisitIndex < visitPath.size() - 1) {
            currentVisitIndex++;
            repaint();
        }
    }

    public void clearVisitPath() {
        this.visitPath.clear();
        this.currentVisitIndex = -1;
        repaint();
    }

    public boolean hasMoreSteps() {
        return currentVisitIndex < visitPath.size() - 1;
    }

    @Override
    protected void paintComponent(Graphics g) {
        super.paintComponent(g);
        if (treeArray == null || treeArray.length <= 1) return;

        Graphics2D g2d = (Graphics2D) g.create();
        g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
        g2d.setFont(new Font("Arial", Font.BOLD, 12));

        // 計算樹的最大層數
        int maxLevel = (int) (Math.log(treeArray.length - 1) / Math.log(2)) + 1;

        // 先畫連接線，再畫節點（避免線條覆蓋節點）
        drawConnections(g2d, maxLevel);
        drawNodes(g2d, maxLevel);

        g2d.dispose();
    }

    private void drawConnections(Graphics2D g2d, int maxLevel) {
        g2d.setColor(Color.BLACK);
        g2d.setStroke(new BasicStroke(2));

        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            int leftChild = 2 * i;
            int rightChild = 2 * i + 1;

            Point parentPos = getNodePosition(i, maxLevel);

            // 畫左子節點連線
            if (leftChild < treeArray.length && treeArray[leftChild] != null) {
                Point leftPos = getNodePosition(leftChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, leftPos.x, leftPos.y));
            }

            // 畫右子節點連線
            if (rightChild < treeArray.length && treeArray[rightChild] != null) {
                Point rightPos = getNodePosition(rightChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, rightPos.x, rightPos.y));
            }
        }
    }

    private void drawNodes(Graphics2D g2d, int maxLevel) {
        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            Point pos = getNodePosition(i, maxLevel);
            boolean isRoot = (i == 1);
            boolean isLeaf = isLeafNode(i);
            boolean isCurrentVisit = currentVisitIndex >= 0 && currentVisitIndex < visitPath.size()
                                   && visitPath.get(currentVisitIndex).equals(treeArray[i]);
            boolean isVisited = isNodeVisited(treeArray[i]);

            // 設定節點顏色
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 215, 0));  // 當前走訪節點：金黃色
            } else if (isVisited) {
                g2d.setColor(new Color(144, 238, 144));  // 已走訪節點：淡綠色
            } else if (isRoot) {
                g2d.setColor(Color.BLACK);  // 根節點黑色背景
            } else if (isLeaf) {
                g2d.setColor(Color.WHITE);  // 葉節點白色背景
            } else {
                g2d.setColor(new Color(200, 200, 200));  // 中間節點灰色背景
            }

            // 畫節點圓圈
            Ellipse2D circle = new Ellipse2D.Double(
                pos.x - NODE_RADIUS, pos.y - NODE_RADIUS,
                2 * NODE_RADIUS, 2 * NODE_RADIUS
            );
            g2d.fill(circle);

            // 畫節點邊框
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 140, 0));  // 當前節點橙色邊框
                g2d.setStroke(new BasicStroke(4));
            } else {
                g2d.setColor(Color.BLACK);
                g2d.setStroke(new BasicStroke(2));
            }
            g2d.draw(circle);

            // 設定文字顏色
            if (isRoot && !isCurrentVisit && !isVisited) {
                g2d.setColor(Color.WHITE);  // 根節點白色文字
            } else {
                g2d.setColor(Color.BLACK);  // 其他節點黑色文字
            }

            // 畫節點值
            String text = treeArray[i].toString();
            FontMetrics fm = g2d.getFontMetrics();
            int textWidth = fm.stringWidth(text);
            int textHeight = fm.getAscent();
            g2d.drawString(text,
                pos.x - textWidth / 2,
                pos.y + textHeight / 2 - 2);

            // 畫陣列編號標示
            g2d.setFont(new Font("Arial", Font.BOLD, 10));
            g2d.setColor(new Color(100, 100, 100));  // 灰色文字
            String indexText = "[" + i + "]";
            FontMetrics indexFm = g2d.getFontMetrics();
            int indexWidth = indexFm.stringWidth(indexText);
            // 將編號放在節點右上角
            g2d.drawString(indexText,
                pos.x + NODE_RADIUS - indexWidth + 8,
                pos.y - NODE_RADIUS + 12);

            // 恢復原本字體
            g2d.setFont(new Font("Arial", Font.BOLD, 12));
        }
    }

    private boolean isNodeVisited(Integer value) {
        for (int i = 0; i <= currentVisitIndex && i < visitPath.size(); i++) {
            if (visitPath.get(i).equals(value)) {
                return true;
            }
        }
        return false;
    }

    private Point getNodePosition(int index, int maxLevel) {
        int level = (int) (Math.log(index) / Math.log(2));
        int nodesInLevel = 1 << level;
        int positionInLevel = index - (1 << level);

        int panelWidth = getWidth();
        int panelHeight = getHeight();

        // 計算水平位置
        int totalWidth = panelWidth - 2 * NODE_RADIUS;
        int spacing = Math.max(MIN_HORIZONTAL_SPACING, totalWidth / (nodesInLevel + 1));
        int x = NODE_RADIUS + spacing * (positionInLevel + 1);

        // 確保節點在面板範圍內
        if (nodesInLevel > 1) {
            x = NODE_RADIUS + (totalWidth * (positionInLevel + 1)) / (nodesInLevel + 1);
        } else {
            x = panelWidth / 2;
        }

        // 計算垂直位置
        int y = NODE_RADIUS + 30 + level * LEVEL_HEIGHT;

        return new Point(x, y);
    }

    private boolean isLeafNode(int index) {
        int leftChild = 2 * index;
        int rightChild = 2 * index + 1;

        boolean hasLeftChild = leftChild < treeArray.length && treeArray[leftChild] != null;
        boolean hasRightChild = rightChild < treeArray.length && treeArray[rightChild] != null;

        return !hasLeftChild && !hasRightChild;
    }
}

import java.util.*;

public class PerformanceTest {

    public static void main(String[] args) {
        System.out.println("割點算法性能測試");
        System.out.println("=".repeat(60));

        // 測試不同規模的圖
        int[] vertices = {10, 20, 50, 100, 200, 500, 1000};
        double[] densities = {0.1, 0.3, 0.5, 0.7}; // 圖的密度 (邊數 / 最大可能邊數)

        System.out.printf("%-10s %-10s %-10s %-15s %-15s %-15s%n", 
                         "Vertices", "Edges", "Density", "Brute(ms)", "Tarjan(ms)", "Speedup");
        System.out.println("-".repeat(80));

        for (int v : vertices) {
            for (double density : densities) {
                int maxEdges = v * (v - 1) / 2; // 完全圖的邊數
                int edges = (int)(maxEdges * density);

                // 生成隨機圖
                GraphGenerator generator = new GraphGenerator(v, edges);
                List<int[]> edgeList = generator.generateConnectedGraph();

                // 測試暴力法
                long bruteTime = testBruteForce(v, edgeList);

                // 測試 Tarjan 算法
                long tarjanTime = testTarjan(v, edgeList);

                // 計算加速比
                double speedup = (double)bruteTime / tarjanTime;

                System.out.printf("%-10d %-10d %-10.1f %-15d %-15d %-15.2fx%n", 
                                 v, edgeList.size(), density, bruteTime, tarjanTime, speedup);

                // 當暴力法時間過長時跳過更大的圖
                if (bruteTime > 5000) { // 超過5秒
                    System.out.println("暴力法時間過長，跳過更大規模的測試");
                    return;
                }
            }
            System.out.println();
        }

        System.out.println("\n結論分析:");
        System.out.println("- 當圖規模較小時 (V < 50)，兩种方法差異不大");
        System.out.println("- 當圖規模較大時 (V >= 100)，Tarjan 算法明顯更快");
        System.out.println("- 建議: V >= 50 或 E >= 500 時使用 Tarjan 算法");
    }

    private static long testBruteForce(int vertices, List<int[]> edges) {
        BruteForceArticulationPoints brute = new BruteForceArticulationPoints(vertices);

        for (int[] edge : edges) {
            brute.addEdge(edge[0], edge[1]);
        }

        long startTime = System.nanoTime();

        // 執行多次測試取平均值
        for (int i = 0; i < 3; i++) {
            brute.findArticulationPoints();
        }

        long endTime = System.nanoTime();
        return (endTime - startTime) / 1_000_000 / 3; // 轉換為毫秒並取平均
    }

    private static long testTarjan(int vertices, List<int[]> edges) {
        TarjanArticulationPoints tarjan = new TarjanArticulationPoints(vertices);

        for (int[] edge : edges) {
            tarjan.addEdge(edge[0], edge[1]);
        }

        long startTime = System.nanoTime();

        // 執行多次測試取平均值
        for (int i = 0; i < 3; i++) {
            tarjan.findArticulationPoints();
        }

        long endTime = System.nanoTime();
        return (endTime - startTime) / 1_000_000 / 3; // 轉換為毫秒並取平均
    }
}

// 圖生成器類別
class GraphGenerator {
    private int vertices;
    private int targetEdges;
    private Random random;

    public GraphGenerator(int vertices, int targetEdges) {
        this.vertices = vertices;
        this.targetEdges = targetEdges;
        this.random = new Random(42); // 固定種子確保結果可重現
    }

    public List<int[]> generateConnectedGraph() {
        Set<String> edgeSet = new HashSet<>();
        List<int[]> edges = new ArrayList<>();

        // 首先創建一個連通的生成樹
        for (int i = 1; i < vertices; i++) {
            int parent = random.nextInt(i);
            String edge = Math.min(i, parent) + "-" + Math.max(i, parent);
            edgeSet.add(edge);
            edges.add(new int[]{i, parent});
        }

        // 添加額外的邊直到達到目標邊數
        while (edges.size() < targetEdges && edges.size() < vertices * (vertices - 1) / 2) {
            int u = random.nextInt(vertices);
            int v = random.nextInt(vertices);

            if (u != v) {
                String edge = Math.min(u, v) + "-" + Math.max(u, v);
                if (!edgeSet.contains(edge)) {
                    edgeSet.add(edge);
                    edges.add(new int[]{u, v});
                }
            }
        }

        return edges;
    }
}

// 暴力法
class BruteForceArticulationPoints {
    private int V;
    private List<List<Integer>> adj;

    public BruteForceArticulationPoints(int V) {
        this.V = V;
        adj = new ArrayList<>();
        for (int i = 0; i < V; i++) adj.add(new ArrayList<>());
    }

    public void addEdge(int u, int v) {
        adj.get(u).add(v);
        adj.get(v).add(u);
    }

    private void dfs(int u, boolean[] visited, int skip) {
        if (u == skip) return;
        visited[u] = true;
        for (int v : adj.get(u)) {
            if (!visited[v] && v != skip) dfs(v, visited, skip);
        }
    }

    public Set<Integer> findArticulationPoints() {
        Set<Integer> result = new HashSet<>();
        for (int i = 0; i < V; i++) {
            boolean[] visited = new boolean[V];
            int start = (i == 0) ? 1 : 0;
            if (start < V) {
                dfs(start, visited, i);
                for (int j = 0; j < V; j++) {
                    if (j != i && !visited[j]) {
                        result.add(i);
                        break;
                    }
                }
            }
        }
        return result;
    }
}

// Tarjan 演算法
class TarjanArticulationPoints {
    private int V, time;
    private List<List<Integer>> adj;
    private boolean[] visited, isAP;
    private int[] disc, low, parent;

    public TarjanArticulationPoints(int V) {
        this.V = V;
        adj = new ArrayList<>();
        for (int i = 0; i < V; i++) adj.add(new ArrayList<>());
    }

    public void addEdge(int u, int v) {
        adj.get(u).add(v);
        adj.get(v).add(u);
    }

    public Set<Integer> findArticulationPoints() {
        time = 0;
        visited = new boolean[V];
        disc = new int[V];
        low = new int[V];
        parent = new int[V];
        isAP = new boolean[V];
        Arrays.fill(parent, -1);

        for (int i = 0; i < V; i++) {
            if (!visited[i]) dfs(i);
        }

        Set<Integer> result = new HashSet<>();
        for (int i = 0; i < V; i++) if (isAP[i]) result.add(i);
        return result;
    }

    private void dfs(int u) {
        visited[u] = true;
        disc[u] = low[u] = ++time;
        int children = 0;
        for (int v : adj.get(u)) {
            if (!visited[v]) {
                children++;
                parent[v] = u;
                dfs(v);
                low[u] = Math.min(low[u], low[v]);
                if (parent[u] == -1 && children > 1) isAP[u] = true;
                if (parent[u] != -1 && low[v] >= disc[u]) isAP[u] = true;
            } else if (v != parent[u]) {
                low[u] = Math.min(low[u], disc[v]);
            }
        }
    }
}

import java.util.*;

public class SimplePerformanceTest {

    public static void main(String[] args) {
        System.out.println("割點算法性能測試比較");
        System.out.println("=".repeat(50));

        // 測試不同規模的圖
        int[] testSizes = {10, 20, 30, 50, 100};

        System.out.printf("%-8s %-8s %-12s %-12s %-10s%n", 
                         "節點數", "邊數", "暴力法(ms)", "Tarjan(ms)", "加速比");
        System.out.println("-".repeat(50));

        for (int v : testSizes) {
            // 創建稠密圖 (約50%密度)
            int edgeCount = v * (v - 1) / 4; // 約一半的可能邊數

            // 生成測試圖
            TestGraph graph = createTestGraph(v, edgeCount);

            // 測試暴力法
            long bruteTime = testBruteForceMethod(graph);

            // 測試 Tarjan 算法  
            long tarjanTime = testTarjanMethod(graph);

            // 計算加速比
            double speedup = (tarjanTime == 0) ? 0 : (double)bruteTime / tarjanTime;

            System.out.printf("%-8d %-8d %-12d %-12d %-10.1fx%n", 
                             v, graph.edges.size(), bruteTime, tarjanTime, speedup);

            // 如果暴力法太慢就停止測試更大的圖
            if (bruteTime > 1000) {
                System.out.println("\n暴力法時間過長，停止測試更大規模的圖");
                break;
            }
        }

        System.out.println("\n性能分析結論:");
        System.out.println("1. 小規模圖 (V ≤ 30): 兩種方法差異不大");
        System.out.println("2. 中等規模圖 (30 < V ≤ 100): Tarjan算法開始顯示優勢");
        System.out.println("3. 大規模圖 (V > 100): 強烈建議使用Tarjan算法");
        System.out.println("\n建議使用原則:");
        System.out.println("- 節點數 ≤ 50: 可使用暴力法 (實現簡單)");
        System.out.println("- 節點數 > 50: 建議使用Tarjan算法 (效能優異)");
    }

    static class TestGraph {
        int vertices;
        List<int[]> edges;

        TestGraph(int v) {
            vertices = v;
            edges = new ArrayList<>();
        }
    }

    static TestGraph createTestGraph(int vertices, int targetEdges) {
        TestGraph graph = new TestGraph(vertices);
        Set<String> edgeSet = new HashSet<>();
        Random rand = new Random(42); // 固定種子保證結果一致

        // 首先確保圖連通 - 創建生成樹
        for (int i = 1; i < vertices; i++) {
            int parent = rand.nextInt(i);
            String edgeKey = Math.min(i, parent) + "-" + Math.max(i, parent);
            if (!edgeSet.contains(edgeKey)) {
                edgeSet.add(edgeKey);
                graph.edges.add(new int[]{i, parent});
            }
        }

        // 添加額外的邊
        while (graph.edges.size() < targetEdges) {
            int u = rand.nextInt(vertices);
            int v = rand.nextInt(vertices);
            if (u != v) {
                String edgeKey = Math.min(u, v) + "-" + Math.max(u, v);
                if (!edgeSet.contains(edgeKey)) {
                    edgeSet.add(edgeKey);
                    graph.edges.add(new int[]{u, v});
                }
            }
        }

        return graph;
    }

    static long testBruteForceMethod(TestGraph graph) {
        BruteForceAP bruteForce = new BruteForceAP(graph.vertices);

        // 建立圖
        for (int[] edge : graph.edges) {
            bruteForce.addEdge(edge[0], edge[1]);
        }

        // 測試執行時間
        long startTime = System.nanoTime();
        bruteForce.findArticulationPoints();
        long endTime = System.nanoTime();

        return (endTime - startTime) / 1_000_000; // 轉換為毫秒
    }

    static long testTarjanMethod(TestGraph graph) {
        TarjanAP tarjan = new TarjanAP(graph.vertices);

        // 建立圖
        for (int[] edge : graph.edges) {
            tarjan.addEdge(edge[0], edge[1]);
        }

        // 測試執行時間
        long startTime = System.nanoTime();
        tarjan.findArticulationPoints();
        long endTime = System.nanoTime();

        return (endTime - startTime) / 1_000_000; // 轉換為毫秒
    }
}

// 暴力法實現
class BruteForceAP {
    private int V;
    private List<List<Integer>> adj;

    public BruteForceAP(int V) {
        this.V = V;
        adj = new ArrayList<>();
        for (int i = 0; i < V; i++) {
            adj.add(new ArrayList<>());
        }
    }

    public void addEdge(int u, int v) {
        adj.get(u).add(v);
        adj.get(v).add(u);
    }

    private void dfs(int u, boolean[] visited, int skip) {
        if (u == skip) return;
        visited[u] = true;
        for (int v : adj.get(u)) {
            if (!visited[v] && v != skip) {
                dfs(v, visited, skip);
            }
        }
    }

    public Set<Integer> findArticulationPoints() {
        Set<Integer> result = new HashSet<>();

        for (int i = 0; i < V; i++) {
            boolean[] visited = new boolean[V];

            // 找一個不是當前測試節點的起始點
            int start = (i == 0) ? 1 : 0;
            if (start >= V) continue;

            // 從起始點開始DFS，跳過當前測試的節點
            dfs(start, visited, i);

            // 檢查是否有節點無法訪問到
            for (int j = 0; j < V; j++) {
                if (j != i && !visited[j]) {
                    result.add(i);
                    break;
                }
            }
        }

        return result;
    }
}

// Tarjan算法實現
class TarjanAP {
    private int V, time;
    private List<List<Integer>> adj;
    private boolean[] visited, isAP;
    private int[] disc, low, parent;

    public TarjanAP(int V) {
        this.V = V;
        adj = new ArrayList<>();
        for (int i = 0; i < V; i++) {
            adj.add(new ArrayList<>());
        }
    }

    public void addEdge(int u, int v) {
        adj.get(u).add(v);
        adj.get(v).add(u);
    }

    public Set<Integer> findArticulationPoints() {
        // 初始化
        time = 0;
        visited = new boolean[V];
        disc = new int[V];
        low = new int[V];
        parent = new int[V];
        isAP = new boolean[V];
        Arrays.fill(parent, -1);

        // 對所有未訪問的節點執行DFS
        for (int i = 0; i < V; i++) {
            if (!visited[i]) {
                dfs(i);
            }
        }

        // 收集所有割點
        Set<Integer> result = new HashSet<>();
        for (int i = 0; i < V; i++) {
            if (isAP[i]) {
                result.add(i);
            }
        }

        return result;
    }

    private void dfs(int u) {
        visited[u] = true;
        disc[u] = low[u] = ++time;
        int children = 0;

        for (int v : adj.get(u)) {
            if (!visited[v]) {
                children++;
                parent[v] = u;
                dfs(v);

                low[u] = Math.min(low[u], low[v]);

                // 根節點是割點的條件：有超過一個子樹
                if (parent[u] == -1 && children > 1) {
                    isAP[u] = true;
                }

                // 非根節點是割點的條件：low[v] >= disc[u]
                if (parent[u] != -1 && low[v] >= disc[u]) {
                    isAP[u] = true;
                }
            } else if (v != parent[u]) {
                // 後向邊：更新low值
                low[u] = Math.min(low[u], disc[v]);
            }
        }
    }
}

import java.awt.*;
import java.util.*;
import java.util.List;

public class Graph {
    private Map<String, Point> nodes = new HashMap<>();
    private List<Edge> edges = new ArrayList<>();

    public Map<String, Point> getNodes() {
        return nodes;
    }

    public List<Edge> getEdges() {
        return edges;
    }

    public void clear() {
        nodes.clear();
        edges.clear();
    }

    public void addNode(String name, Point p) {
        nodes.put(name, p);
    }

    public void removeNode(String name) {
        nodes.remove(name);
        edges.removeIf(edge -> edge.from.equals(name) || edge.to.equals(name));
    }

    public void addEdge(String from, String to) {
        Random rand = new Random();
        int cost = rand.nextInt(99) + 1; // 1~99
        Edge edge = new Edge(from, to, cost);
        if (!edges.contains(edge)) {
            edges.add(edge);
        }
    }

    public void addEdge(String from, String to, int cost) {
        Edge edge = new Edge(from, to, cost);
        if (!edges.contains(edge)) {
            edges.add(edge);
        }
    }

    public boolean containsNode(String name) {
        return nodes.containsKey(name);
    }

    public Map<String, List<String>> getAdjacencyList() {
        Map<String, List<String>> graph = new HashMap<>();
        for (String name : nodes.keySet()) {
            graph.put(name, new ArrayList<>());
        }
        for (Edge edge : edges) {
            if (graph.containsKey(edge.from) && graph.containsKey(edge.to)) {
                graph.get(edge.from).add(edge.to);
                graph.get(edge.to).add(edge.from);
            }
        }
        return graph;
    }

    public List<String> dfs(String start) {
        Map<String, List<String>> graph = getAdjacencyList();
        List<String> result = new ArrayList<>();
        Set<String> visited = new HashSet<>();
        dfsHelper(start, graph, visited, result);
        return result;
    }

    private void dfsHelper(String node, Map<String, List<String>> graph, Set<String> visited, List<String> result) {
        if (!visited.contains(node)) {
            visited.add(node);
            result.add(node);
            for (String neighbor : graph.get(node)) {
                dfsHelper(neighbor, graph, visited, result);
            }
        }
    }

    public List<String> bfs(String start) {
        Map<String, List<String>> graph = getAdjacencyList();
        List<String> result = new ArrayList<>();
        Set<String> visited = new HashSet<>();
        Queue<String> queue = new LinkedList<>();
        queue.add(start);
        visited.add(start);
        while (!queue.isEmpty()) {
            String node = queue.poll();
            result.add(node);
            for (String neighbor : graph.get(node)) {
                if (!visited.contains(neighbor)) {
                    queue.add(neighbor);
                    visited.add(neighbor);
                }
            }
        }
        return result;
    }

    // Kruskal's algorithm for MST
    public List<Edge> getMST() {
        List<Edge> result = new ArrayList<>();
        Map<String, String> parent = new HashMap<>();
        for (String node : nodes.keySet()) parent.put(node, node);
        List<Edge> sortedEdges = new ArrayList<>(edges);
        sortedEdges.sort(Comparator.comparingInt(e -> e.cost));
        int count = 0;
        for (Edge edge : sortedEdges) {
            String root1 = find(parent, edge.from);
            String root2 = find(parent, edge.to);
            if (!root1.equals(root2)) {
                result.add(edge);
                parent.put(root1, root2);
                count++;
                if (count == nodes.size() - 1) break;
            }
        }
        return result;
    }

    private String find(Map<String, String> parent, String node) {
        if (!parent.get(node).equals(node)) {
            parent.put(node, find(parent, parent.get(node)));
        }
        return parent.get(node);
    }

    // Dijkstra's algorithm for shortest path
    public List<Edge> getShortestPath(String from, String to) {
        Map<String, Integer> dist = new HashMap<>();
        Map<String, String> prev = new HashMap<>();
        for (String node : nodes.keySet()) dist.put(node, Integer.MAX_VALUE);
        dist.put(from, 0);
        PriorityQueue<String> pq = new PriorityQueue<>(Comparator.comparingInt(dist::get));
        pq.add(from);

        while (!pq.isEmpty()) {
            String u = pq.poll();
            for (Edge edge : edges) {
                String v = null;
                if (edge.from.equals(u)) v = edge.to;
                else if (edge.to.equals(u)) v = edge.from;
                if (v != null) {
                    int alt = dist.get(u) + edge.cost;
                    if (alt < dist.get(v)) {
                        dist.put(v, alt);
                        prev.put(v, u);
                        pq.add(v);
                    }
                }
            }
        }

        // 回溯路徑
        List<Edge> path = new ArrayList<>();
        String curr = to;
        while (prev.containsKey(curr)) {
            String p = prev.get(curr);
            // 找到對應的邊
            for (Edge edge : edges) {
                if ((edge.from.equals(curr) && edge.to.equals(p)) ||
                    (edge.from.equals(p) && edge.to.equals(curr))) {
                    path.add(0, edge);
                    break;
                }
            }
            curr = p;
        }
        if (!from.equals(to) && path.isEmpty()) return null; // 無路徑
        return path;
    }
}

import java.awt.*;
import java.util.*;
import java.util.List;

public class Graph {
    private Map<String, Point> nodes = new HashMap<>();
    private List<Edge> edges = new ArrayList<>();

    public Map<String, Point> getNodes() {
        return nodes;
    }

    public List<Edge> getEdges() {
        return edges;
    }

    public void clear() {
        nodes.clear();
        edges.clear();
    }

    public void addNode(String name, Point p) {
        nodes.put(name, p);
    }

    public void removeNode(String name) {
        nodes.remove(name);
        edges.removeIf(edge -> edge.from.equals(name) || edge.to.equals(name));
    }

    public void addEdge(String from, String to) {
        Random rand = new Random();
        int cost = rand.nextInt(99) + 1; // 1~99
        Edge edge = new Edge(from, to, cost);
        if (!edges.contains(edge)) {
            edges.add(edge);
        }
    }

    public void addEdge(String from, String to, int cost) {
        Edge edge = new Edge(from, to, cost);
        if (!edges.contains(edge)) {
            edges.add(edge);
        }
    }

    public boolean containsNode(String name) {
        return nodes.containsKey(name);
    }

    public int[][] getAdjacencyMatrix() {
        List<String> nodeNames = new ArrayList<>(nodes.keySet());
        Collections.sort(nodeNames); // 排序確保矩陣順序一致
        int n = nodeNames.size();
        int[][] matrix = new int[n][n];

        // 初始化矩陣
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                matrix[i][j] = 0;
            }
        }

        // 填入邊的權重
        for (Edge edge : edges) {
            int fromIndex = nodeNames.indexOf(edge.from);
            int toIndex = nodeNames.indexOf(edge.to);
            if (fromIndex >= 0 && toIndex >= 0) {
                matrix[fromIndex][toIndex] = edge.cost;
                matrix[toIndex][fromIndex] = edge.cost; // 無向圖
            }
        }

        return matrix;
    }

    public String getAdjacencyMatrixString() {
        if (nodes.isEmpty()) return "No nodes in graph";

        List<String> nodeNames = new ArrayList<>(nodes.keySet());
        Collections.sort(nodeNames);
        int[][] matrix = getAdjacencyMatrix();

        StringBuilder sb = new StringBuilder();

        // 標題行
        sb.append("    ");
        for (String name : nodeNames) {
            sb.append(String.format("%4s", name));
        }
        sb.append("\n");

        // 矩陣行
        for (int i = 0; i < nodeNames.size(); i++) {
            sb.append(String.format("%4s", nodeNames.get(i)));
            for (int j = 0; j < nodeNames.size(); j++) {
                sb.append(String.format("%4d", matrix[i][j]));
            }
            sb.append("\n");
        }

        return sb.toString();
    }

    public Map<String, List<String>> getAdjacencyList() {
        Map<String, List<String>> graph = new HashMap<>();
        for (String name : nodes.keySet()) {
            graph.put(name, new ArrayList<>());
        }
        for (Edge edge : edges) {
            if (graph.containsKey(edge.from) && graph.containsKey(edge.to)) {
                graph.get(edge.from).add(edge.to);
                graph.get(edge.to).add(edge.from);
            }
        }
        return graph;
    }

    public List<String> dfs(String start) {
        Map<String, List<String>> graph = getAdjacencyList();
        List<String> result = new ArrayList<>();
        Set<String> visited = new HashSet<>();
        dfsHelper(start, graph, visited, result);
        return result;
    }

    private void dfsHelper(String node, Map<String, List<String>> graph, Set<String> visited, List<String> result) {
        if (!visited.contains(node)) {
            visited.add(node);
            result.add(node);
            for (String neighbor : graph.get(node)) {
                dfsHelper(neighbor, graph, visited, result);
            }
        }
    }

    public List<String> bfs(String start) {
        Map<String, List<String>> graph = getAdjacencyList();
        List<String> result = new ArrayList<>();
        Set<String> visited = new HashSet<>();
        Queue<String> queue = new LinkedList<>();
        queue.add(start);
        visited.add(start);
        while (!queue.isEmpty()) {
            String node = queue.poll();
            result.add(node);
            for (String neighbor : graph.get(node)) {
                if (!visited.contains(neighbor)) {
                    queue.add(neighbor);
                    visited.add(neighbor);
                }
            }
        }
        return result;
    }

    // Kruskal's algorithm for MST
    public List<Edge> getMST() {
        List<Edge> result = new ArrayList<>();
        Map<String, String> parent = new HashMap<>();
        for (String node : nodes.keySet()) parent.put(node, node);
        List<Edge> sortedEdges = new ArrayList<>(edges);
        sortedEdges.sort(Comparator.comparingInt(e -> e.cost));
        int count = 0;
        for (Edge edge : sortedEdges) {
            String root1 = find(parent, edge.from);
            String root2 = find(parent, edge.to);
            if (!root1.equals(root2)) {
                result.add(edge);
                parent.put(root1, root2);
                count++;
                if (count == nodes.size() - 1) break;
            }
        }
        return result;
    }

    private String find(Map<String, String> parent, String node) {
        if (!parent.get(node).equals(node)) {
            parent.put(node, find(parent, parent.get(node)));
        }
        return parent.get(node);
    }

    // Dijkstra's algorithm for shortest path
    public List<Edge> getShortestPath(String from, String to) {
        Map<String, Integer> dist = new HashMap<>();
        Map<String, String> prev = new HashMap<>();
        for (String node : nodes.keySet()) dist.put(node, Integer.MAX_VALUE);
        dist.put(from, 0);
        PriorityQueue<String> pq = new PriorityQueue<>(Comparator.comparingInt(dist::get));
        pq.add(from);

        while (!pq.isEmpty()) {
            String u = pq.poll();
            for (Edge edge : edges) {
                String v = null;
                if (edge.from.equals(u)) v = edge.to;
                else if (edge.to.equals(u)) v = edge.from;
                if (v != null) {
                    int alt = dist.get(u) + edge.cost;
                    if (alt < dist.get(v)) {
                        dist.put(v, alt);
                        prev.put(v, u);
                        pq.add(v);
                    }
                }
            }
        }

        // 回溯路徑
        List<Edge> path = new ArrayList<>();
        String curr = to;
        while (prev.containsKey(curr)) {
            String p = prev.get(curr);
            // 找到對應的邊
            for (Edge edge : edges) {
                if ((edge.from.equals(curr) && edge.to.equals(p)) ||
                    (edge.from.equals(p) && edge.to.equals(curr))) {
                    path.add(0, edge);
                    break;
                }
            }
            curr = p;
        }
        if (!from.equals(to) && path.isEmpty()) return null; // 無路徑
        return path;
    }
}

