        resetBtn.setEnabled(false);

        visitGbc.gridx = 0; visitGbc.gridy = 0; visitGbc.gridwidth = 3;
        visitPanel.add(inorderBtn, visitGbc);
        visitGbc.gridy = 1;
        visitPanel.add(preorderBtn, visitGbc);
        visitGbc.gridy = 2;
