        // 輸出結果區
        JPanel outputPanel = new JPanel(new BorderLayout());
        outputPanel.setBorder(BorderFactory.createTitledBorder(
            BorderFactory.createEtchedBorder(), "走訪結果",
            0, 0, new Font("Arial", Font.BOLD, 14)));
        outputPanel.setBackground(Color.WHITE);

        outputArea = new JTextArea(6, 80);
        outputArea.setEditable(false);
        outputArea.setFont(new Font("Courier New", Font.PLAIN, 12));
        outputArea.setBackground(new Color(248, 249, 250));
        outputArea.setBorder(BorderFactory.createEmptyBorder(5, 5, 5, 5));
        outputPanel.add(new JScrollPane(outputArea), BorderLayout.CENTER);

        bottomPanel.add(outputPanel, BorderLayout.CENTER);
        add(bottomPanel, BorderLayout.SOUTH);

        // 事件處理
        buildBtn.addActionListener(e -> buildTreeFromInput());
        autoBtn.addActionListener(e -> buildTreeAuto(autoNField.getText()));
        insertBtn.addActionListener(e -> insertNode());
        deleteBtn.addActionListener(e -> deleteNode());
        visitBtn.addActionListener(e -> visitTree());
        stepBtn.addActionListener(e -> stepTraversal());
        resetBtn.addActionListener(e -> resetTraversal());

        // 初始化 Timer
        walkTimer = new javax.swing.Timer(800, e -> {
            if (treePanel.hasMoreSteps()) {
                treePanel.showNextVisitStep();
            } else {
                walkTimer.stop();
                stepBtn.setEnabled(false);
            }
        });

        // 設定視窗居中
        setLocationRelativeTo(null);
    }

    private void buildTreeFromInput() {
        String input = inputField.getText().trim();
        arr = BinaryTreeArray.parseInput(input);
        tree = new BinaryTreeArray(arr);
        outputArea.setText("建立樹的陣列參數: " + Arrays.toString(Arrays.copyOfRange(arr, 1, arr.length)));
        updateDisplay(false);
    }

    private void buildTreeAuto(String nStr) {
        int n;
        try {
            n = Integer.parseInt(nStr.trim());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "請輸入正整數N");
            return;
        }
        if (n <= 0) {
            JOptionPane.showMessageDialog(this, "N必須大於0");
            return;
        }
        Set<Integer> nums = new LinkedHashSet<>();
        Random rand = new Random();
        while (nums.size() < n) nums.add(rand.nextInt(10000));
        arr = new Integer[n + 1];
        arr[0] = n;
        int i = 1;
        for (int num : nums) arr[i++] = num;
        tree = new BinaryTreeArray(arr);
        inputField.setText("");
        outputArea.setText("建立樹的陣列參數: " + Arrays.toString(Arrays.copyOfRange(arr, 1, arr.length)));
        updateDisplay(false);
    }

    private void insertNode() {
        if (arr == null) return;
        int idx;
        try {
            idx = Integer.parseInt(insertIdxField.getText().trim());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "請輸入索引");
            return;
        }
        String valStr = insertValField.getText().trim();
        Integer val = valStr.equalsIgnoreCase("null") ? null : null;
        if (!valStr.equalsIgnoreCase("null")) {
            try {
                val = Integer.parseInt(valStr);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(this, "請輸入整數或null");
                return;
            }
        }
        if (idx > 0 && idx < arr.length) {
            if (arr[idx] == null && val != null) arr[0]++;
            if (arr[idx] != null && val == null) arr[0]--;
            arr[idx] = val;
            tree = new BinaryTreeArray(arr);
            updateDisplay(false);
        } else {
            JOptionPane.showMessageDialog(this, "索引超出範圍");
        }
    }

    private void deleteNode() {
        if (arr == null) return;
        int idx;
        try {
            idx = Integer.parseInt(deleteIdxField.getText().trim());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "請輸入索引");
            return;
        }
        int[] delCount = new int[]{0};
        countDelete(arr, idx, delCount);
        tree.delete(idx);
        arr[0] -= delCount[0];
        updateDisplay(false);
    }

    private void updateDisplay(boolean showTraversal) {
        if (arr == null) return;
        nodeCountLabel.setText("節點數: " + arr[0]);

        // 更新樹狀視覺化
        treePanel.setTreeArray(arr);

        if (tree != null && showTraversal) {
            StringBuilder sb = new StringBuilder();
            if (inorderBtn.isSelected()) {
                sb.append("中序: ").append(tree.inorder()).append("\n");
            }
            if (preorderBtn.isSelected()) {
                sb.append("前序: ").append(tree.preorder()).append("\n");
            }
            if (postorderBtn.isSelected()) {
                sb.append("後序: ").append(tree.postorder()).append("\n");
            }
            if (sb.length() == 0) {
                sb.append("請至少選擇一種走訪方式");
            }
            outputArea.setText(sb.toString());
        }
    }

    // 遞迴計算將被刪除的節點數
    private static void countDelete(Integer[] arr, int idx, int[] count) {
        if (idx <= 0 || idx >= arr.length || arr[idx] == null) return;
        count[0]++;
        countDelete(arr, 2 * idx, count);
        countDelete(arr, 2 * idx + 1, count);
    }

    private void visitTree() {
        if (tree == null) return;

        currentTraversalPath = new ArrayList<>();
        StringBuilder sb = new StringBuilder();

        if (inorderBtn.isSelected()) {
            java.util.List<Integer> inorderResult = tree.inorder();
            currentTraversalPath.addAll(inorderResult);
            sb.append("中序: ").append(inorderResult).append("\n");
        }
        if (preorderBtn.isSelected()) {
            java.util.List<Integer> preorderResult = tree.preorder();
            if (!inorderBtn.isSelected()) currentTraversalPath.addAll(preorderResult);
            sb.append("前序: ").append(preorderResult).append("\n");
        }
        if (postorderBtn.isSelected()) {
            java.util.List<Integer> postorderResult = tree.postorder();
            if (!inorderBtn.isSelected() && !preorderBtn.isSelected()) currentTraversalPath.addAll(postorderResult);
            sb.append("後序: ").append(postorderResult).append("\n");
        }

        if (sb.length() == 0) {
            sb.append("請至少選擇一種走訪方式");
            outputArea.setText(sb.toString());
            return;
        }

        // 設定走訪路徑到視覺化面板
        treePanel.setVisitPath(currentTraversalPath);
        outputArea.setText(sb.toString());

        // 啟用控制按鈕
        stepBtn.setEnabled(true);
        resetBtn.setEnabled(true);
    }

    private void stepTraversal() {
        if (currentTraversalPath != null && !currentTraversalPath.isEmpty()) {
            if (treePanel.hasMoreSteps()) {
                treePanel.showNextVisitStep();
                if (!treePanel.hasMoreSteps()) {
                    stepBtn.setEnabled(false);
                }
            }
        }
    }

    private void resetTraversal() {
        if (walkTimer.isRunning()) {
            walkTimer.stop();
        }
        treePanel.clearVisitPath();
        stepBtn.setEnabled(currentTraversalPath != null && !currentTraversalPath.isEmpty());
        resetBtn.setEnabled(false);
    }

    public static void main(String[] args) {
        SwingUtilities.invokeLater(() -> {
            BinaryTreeArrayGUI gui = new BinaryTreeArrayGUI();
            gui.setVisible(true);
        });
    }
}

import javax.swing.*;
import java.awt.*;
import java.awt.geom.Ellipse2D;
import java.awt.geom.Line2D;
import java.util.ArrayList;

public class TreeVisualizationPanel extends JPanel {
    private Integer[] treeArray;
    private java.util.List<Integer> visitPath;
    private int currentVisitIndex;
    private static final int NODE_RADIUS = 20;
    private static final int LEVEL_HEIGHT = 80;
    private static final int MIN_HORIZONTAL_SPACING = 50;

    public TreeVisualizationPanel() {
        setBackground(new Color(248, 249, 250));
        setPreferredSize(new Dimension(600, 400));
        visitPath = new ArrayList<>();
        currentVisitIndex = -1;
    }

    public void setTreeArray(Integer[] arr) {
        this.treeArray = arr;
        clearVisitPath();
        repaint();
    }

    public void setVisitPath(java.util.List<Integer> path) {
        this.visitPath = path;
        this.currentVisitIndex = -1;
        repaint();
    }

    public void showNextVisitStep() {
        if (currentVisitIndex < visitPath.size() - 1) {
            currentVisitIndex++;
            repaint();
        }
    }

    public void clearVisitPath() {
        this.visitPath.clear();
        this.currentVisitIndex = -1;
        repaint();
    }

    public boolean hasMoreSteps() {
        return currentVisitIndex < visitPath.size() - 1;
    }

    @Override
    protected void paintComponent(Graphics g) {
        super.paintComponent(g);
        if (treeArray == null || treeArray.length <= 1) return;

        Graphics2D g2d = (Graphics2D) g.create();
        g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
        g2d.setFont(new Font("Arial", Font.BOLD, 12));

        // 計算樹的最大層數
        int maxLevel = (int) (Math.log(treeArray.length - 1) / Math.log(2)) + 1;

        // 先畫連接線，再畫節點（避免線條覆蓋節點）
        drawConnections(g2d, maxLevel);
        drawNodes(g2d, maxLevel);

        g2d.dispose();
    }

    private void drawConnections(Graphics2D g2d, int maxLevel) {
        g2d.setColor(Color.BLACK);
        g2d.setStroke(new BasicStroke(2));

        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            int leftChild = 2 * i;
            int rightChild = 2 * i + 1;

            Point parentPos = getNodePosition(i, maxLevel);

            // 畫左子節點連線
            if (leftChild < treeArray.length && treeArray[leftChild] != null) {
                Point leftPos = getNodePosition(leftChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, leftPos.x, leftPos.y));
            }

            // 畫右子節點連線
            if (rightChild < treeArray.length && treeArray[rightChild] != null) {
                Point rightPos = getNodePosition(rightChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, rightPos.x, rightPos.y));
            }
        }
    }

    private void drawNodes(Graphics2D g2d, int maxLevel) {
        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            Point pos = getNodePosition(i, maxLevel);
            boolean isRoot = (i == 1);
            boolean isLeaf = isLeafNode(i);
            boolean isCurrentVisit = currentVisitIndex >= 0 && currentVisitIndex < visitPath.size()
                                   && visitPath.get(currentVisitIndex).equals(treeArray[i]);
            boolean isVisited = isNodeVisited(treeArray[i]);

            // 設定節點顏色
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 215, 0));  // 當前走訪節點：金黃色
            } else if (isVisited) {
                g2d.setColor(new Color(144, 238, 144));  // 已走訪節點：淡綠色
            } else if (isRoot) {
                g2d.setColor(Color.BLACK);  // 根節點黑色背景
            } else if (isLeaf) {
                g2d.setColor(Color.WHITE);  // 葉節點白色背景
            } else {
                g2d.setColor(new Color(200, 200, 200));  // 中間節點灰色背景
            }

            // 畫節點圓圈
            Ellipse2D circle = new Ellipse2D.Double(
                pos.x - NODE_RADIUS, pos.y - NODE_RADIUS,
                2 * NODE_RADIUS, 2 * NODE_RADIUS
            );
            g2d.fill(circle);

            // 畫節點邊框
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 140, 0));  // 當前節點橙色邊框
                g2d.setStroke(new BasicStroke(4));
            } else {
                g2d.setColor(Color.BLACK);
                g2d.setStroke(new BasicStroke(2));
            }
            g2d.draw(circle);

            // 設定文字顏色
            if (isRoot && !isCurrentVisit && !isVisited) {
                g2d.setColor(Color.WHITE);  // 根節點白色文字
            } else {
                g2d.setColor(Color.BLACK);  // 其他節點黑色文字
            }

            // 畫節點值
            String text = treeArray[i].toString();
            FontMetrics fm = g2d.getFontMetrics();
            int textWidth = fm.stringWidth(text);
            int textHeight = fm.getAscent();
            g2d.drawString(text,
                pos.x - textWidth / 2,
                pos.y + textHeight / 2 - 2);
        }
    }

    private boolean isNodeVisited(Integer value) {
        for (int i = 0; i <= currentVisitIndex && i < visitPath.size(); i++) {
            if (visitPath.get(i).equals(value)) {
                return true;
            }
        }
        return false;
    }

    private Point getNodePosition(int index, int maxLevel) {
        int level = (int) (Math.log(index) / Math.log(2));
        int nodesInLevel = 1 << level;
        int positionInLevel = index - (1 << level);

        int panelWidth = getWidth();
        int panelHeight = getHeight();

        // 計算水平位置
        int totalWidth = panelWidth - 2 * NODE_RADIUS;
        int spacing = Math.max(MIN_HORIZONTAL_SPACING, totalWidth / (nodesInLevel + 1));
        int x = NODE_RADIUS + spacing * (positionInLevel + 1);

        // 確保節點在面板範圍內
        if (nodesInLevel > 1) {
            x = NODE_RADIUS + (totalWidth * (positionInLevel + 1)) / (nodesInLevel + 1);
        } else {
            x = panelWidth / 2;
        }

        // 計算垂直位置
        int y = NODE_RADIUS + 30 + level * LEVEL_HEIGHT;

        return new Point(x, y);
    }

    private boolean isLeafNode(int index) {
        int leftChild = 2 * index;
        int rightChild = 2 * index + 1;

        boolean hasLeftChild = leftChild < treeArray.length && treeArray[leftChild] != null;
        boolean hasRightChild = rightChild < treeArray.length && treeArray[rightChild] != null;

        return !hasLeftChild && !hasRightChild;
    }
}

import javax.swing.*;
import java.awt.*;
import java.awt.geom.Ellipse2D;
import java.awt.geom.Line2D;
import java.util.ArrayList;

public class TreeVisualizationPanel extends JPanel {
    private Integer[] treeArray;
    private java.util.List<Integer> visitPath;
    private int currentVisitIndex;
    private static final int NODE_RADIUS = 20;
    private static final int LEVEL_HEIGHT = 80;
    private static final int MIN_HORIZONTAL_SPACING = 50;

    public TreeVisualizationPanel() {
        setBackground(new Color(248, 249, 250));
        setPreferredSize(new Dimension(600, 400));
        visitPath = new ArrayList<>();
        currentVisitIndex = -1;
    }

    public void setTreeArray(Integer[] arr) {
        this.treeArray = arr;
        clearVisitPath();
        repaint();
    }

    public void setVisitPath(java.util.List<Integer> path) {
        this.visitPath = path;
        this.currentVisitIndex = -1;
        repaint();
    }

    public void showNextVisitStep() {
        if (currentVisitIndex < visitPath.size() - 1) {
            currentVisitIndex++;
            repaint();
        }
    }

    public void clearVisitPath() {
        this.visitPath.clear();
        this.currentVisitIndex = -1;
        repaint();
    }

    public boolean hasMoreSteps() {
        return currentVisitIndex < visitPath.size() - 1;
    }

    @Override
    protected void paintComponent(Graphics g) {
        super.paintComponent(g);
        if (treeArray == null || treeArray.length <= 1) return;

        Graphics2D g2d = (Graphics2D) g.create();
        g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
        g2d.setFont(new Font("Arial", Font.BOLD, 12));

        // 計算樹的最大層數
        int maxLevel = (int) (Math.log(treeArray.length - 1) / Math.log(2)) + 1;

        // 先畫連接線，再畫節點（避免線條覆蓋節點）
        drawConnections(g2d, maxLevel);
        drawNodes(g2d, maxLevel);

        g2d.dispose();
    }

    private void drawConnections(Graphics2D g2d, int maxLevel) {
        g2d.setColor(Color.BLACK);
        g2d.setStroke(new BasicStroke(2));

        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            int leftChild = 2 * i;
            int rightChild = 2 * i + 1;

            Point parentPos = getNodePosition(i, maxLevel);

            // 畫左子節點連線
            if (leftChild < treeArray.length && treeArray[leftChild] != null) {
                Point leftPos = getNodePosition(leftChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, leftPos.x, leftPos.y));
            }

            // 畫右子節點連線
            if (rightChild < treeArray.length && treeArray[rightChild] != null) {
                Point rightPos = getNodePosition(rightChild, maxLevel);
                g2d.draw(new Line2D.Double(parentPos.x, parentPos.y, rightPos.x, rightPos.y));
            }
        }
    }

    private void drawNodes(Graphics2D g2d, int maxLevel) {
        for (int i = 1; i < treeArray.length; i++) {
            if (treeArray[i] == null) continue;

            Point pos = getNodePosition(i, maxLevel);
            boolean isRoot = (i == 1);
            boolean isLeaf = isLeafNode(i);
            boolean isCurrentVisit = currentVisitIndex >= 0 && currentVisitIndex < visitPath.size()
                                   && visitPath.get(currentVisitIndex).equals(treeArray[i]);
            boolean isVisited = isNodeVisited(treeArray[i]);

            // 設定節點顏色
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 215, 0));  // 當前走訪節點：金黃色
            } else if (isVisited) {
                g2d.setColor(new Color(144, 238, 144));  // 已走訪節點：淡綠色
            } else if (isRoot) {
                g2d.setColor(Color.BLACK);  // 根節點黑色背景
            } else if (isLeaf) {
                g2d.setColor(Color.WHITE);  // 葉節點白色背景
            } else {
                g2d.setColor(new Color(200, 200, 200));  // 中間節點灰色背景
            }

            // 畫節點圓圈
            Ellipse2D circle = new Ellipse2D.Double(
                pos.x - NODE_RADIUS, pos.y - NODE_RADIUS,
                2 * NODE_RADIUS, 2 * NODE_RADIUS
            );
            g2d.fill(circle);

            // 畫節點邊框
            if (isCurrentVisit) {
                g2d.setColor(new Color(255, 140, 0));  // 當前節點橙色邊框
                g2d.setStroke(new BasicStroke(4));
            } else {
                g2d.setColor(Color.BLACK);
                g2d.setStroke(new BasicStroke(2));
            }
            g2d.draw(circle);

            // 設定文字顏色
            if (isRoot && !isCurrentVisit && !isVisited) {
                g2d.setColor(Color.WHITE);  // 根節點白色文字
            } else {
                g2d.setColor(Color.BLACK);  // 其他節點黑色文字
            }

            // 畫節點值
            String text = treeArray[i].toString();
            FontMetrics fm = g2d.getFontMetrics();
            int textWidth = fm.stringWidth(text);
            int textHeight = fm.getAscent();
            g2d.drawString(text,
                pos.x - textWidth / 2,
                pos.y + textHeight / 2 - 2);

            // 畫陣列編號標示
            g2d.setFont(new Font("Arial", Font.BOLD, 10));
            g2d.setColor(new Color(100, 100, 100));  // 灰色文字
            String indexText = "[" + i + "]";
            FontMetrics indexFm = g2d.getFontMetrics();
            int indexWidth = indexFm.stringWidth(indexText);
            // 將編號放在節點右上角
            g2d.drawString(indexText,
                pos.x + NODE_RADIUS - indexWidth + 8,
                pos.y - NODE_RADIUS + 12);

            // 恢復原本字體
            g2d.setFont(new Font("Arial", Font.BOLD, 12));
        }
    }

    private boolean isNodeVisited(Integer value) {
        for (int i = 0; i <= currentVisitIndex && i < visitPath.size(); i++) {
            if (visitPath.get(i).equals(value)) {
                return true;
            }
        }
        return false;
    }

    private Point getNodePosition(int index, int maxLevel) {
        int level = (int) (Math.log(index) / Math.log(2));
        int nodesInLevel = 1 << level;
        int positionInLevel = index - (1 << level);

        int panelWidth = getWidth();
        int panelHeight = getHeight();

        // 計算水平位置
        int totalWidth = panelWidth - 2 * NODE_RADIUS;
        int spacing = Math.max(MIN_HORIZONTAL_SPACING, totalWidth / (nodesInLevel + 1));
        int x = NODE_RADIUS + spacing * (positionInLevel + 1);

        // 確保節點在面板範圍內
        if (nodesInLevel > 1) {
            x = NODE_RADIUS + (totalWidth * (positionInLevel + 1)) / (nodesInLevel + 1);
        } else {
            x = panelWidth / 2;
        }

        // 計算垂直位置
        int y = NODE_RADIUS + 30 + level * LEVEL_HEIGHT;

        return new Point(x, y);
    }

    private boolean isLeafNode(int index) {
        int leftChild = 2 * index;
        int rightChild = 2 * index + 1;

        boolean hasLeftChild = leftChild < treeArray.length && treeArray[leftChild] != null;
        boolean hasRightChild = rightChild < treeArray.length && treeArray[rightChild] != null;

        return !hasLeftChild && !hasRightChild;
    }
}

import java.util.Objects;

public class Edge {
    public String from, to;
    public Edge(String f, String t) { from = f; to = t; }
    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Edge edge = (Edge) o;
        return Objects.equals(from, edge.from) && Objects.equals(to, edge.to);
    }
    @Override
    public int hashCode() {
        return Objects.hash(from, to);
    }
}

import java.awt.*;
import java.util.*;

public class Graph {
    private Map<String, Point> nodes = new HashMap<>();
    private java.util.List<Edge> edges = new ArrayList<>();

    public Map<String, Point> getNodes() {
        return nodes;
    }

    public java.util.List<Edge> getEdges() {
        return edges;
    }

    public void clear() {
        nodes.clear();
        edges.clear();
    }

    public void addNode(String name, Point p) {
        nodes.put(name, p);
    }

    public void removeNode(String name) {
        nodes.remove(name);
        edges.removeIf(edge -> edge.from.equals(name) || edge.to.equals(name));
    }

    public void addEdge(String from, String to) {
        Edge edge = new Edge(from, to);
        if (!edges.contains(edge)) {
            edges.add(edge);
        }
    }

    public boolean containsNode(String name) {
        return nodes.containsKey(name);
    }

    public Map<String, java.util.List<String>> getAdjacencyList() {
        Map<String, java.util.List<String>> graph = new HashMap<>();
        for (String name : nodes.keySet()) {
            graph.put(name, new ArrayList<>());
        }
        for (Edge edge : edges) {
            if (graph.containsKey(edge.from) && graph.containsKey(edge.to)) {
                graph.get(edge.from).add(edge.to);
                graph.get(edge.to).add(edge.from);
            }
        }
        return graph;
    }

    public java.util.List<String> dfs(String start) {
        Map<String, java.util.List<String>> graph = getAdjacencyList();
        java.util.List<String> result = new ArrayList<>();
        Set<String> visited = new HashSet<>();
        dfsHelper(start, graph, visited, result);
        return result;
    }

    private void dfsHelper(String node, Map<String, java.util.List<String>> graph, Set<String> visited, java.util.List<String> result) {
        if (!visited.contains(node)) {
            visited.add(node);
            result.add(node);
            for (String neighbor : graph.get(node)) {
                dfsHelper(neighbor, graph, visited, result);
            }
        }
    }

    public java.util.List<String> bfs(String start) {
        Map<String, java.util.List<String>> graph = getAdjacencyList();
        java.util.List<String> result = new ArrayList<>();
        Set<String> visited = new HashSet<>();
        Queue<String> queue = new LinkedList<>();
        queue.add(start);
        visited.add(start);
        while (!queue.isEmpty()) {
            String node = queue.poll();
            result.add(node);
            for (String neighbor : graph.get(node)) {
                if (!visited.contains(neighbor)) {
                    queue.add(neighbor);
                    visited.add(neighbor);
                }
            }
        }
        return result;
    }
}

import javax.swing.*;
import java.awt.*;
import java.awt.event.*;
import java.util.*;

public class GraphGUI extends JFrame {
  // 節點資料(Key:名稱, Value:位置)
  private Map<String, Point> nodes = new HashMap<>();
  // 邊資料
  private java.util.List<Edge> edges = new ArrayList<>();
  // 介面元件
  private JTextField nodeNameField = new JTextField(5);
  private JTextField fromField = new JTextField(3);
  private JTextField toField = new JTextField(3);
  private JTextField nodeCountField = new JTextField(3);
  private JTextField edgeCountField = new JTextField(3);
  private JTextField startNodeField = new JTextField(3);
  private java.util.List<String> traversalResult = new ArrayList<>();
  private int animationIndex = 0;
  private javax.swing.Timer animationTimer;


  public GraphGUI() {
    setTitle("Graph Application");
    setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    setSize(1500, 600);

    GraphPanel panel = new GraphPanel();
    add(panel, BorderLayout.CENTER);

    // 將控制區放到右側
    JPanel controlPanel = new JPanel(new GridBagLayout());
    GridBagConstraints gbc = new GridBagConstraints();
    gbc.insets = new Insets(2, 2, 2, 2);
    gbc.fill = GridBagConstraints.HORIZONTAL;
    gbc.gridy = 0; gbc.gridx = 0;
    controlPanel.add(new JLabel("Node Name:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(nodeNameField, gbc);

    JButton addNodeBtn = new JButton("Add Node");
    JButton removeNodeBtn = new JButton("Remove Node");

    addNodeBtn.addActionListener(e -> {
      String name = nodeNameField.getText().trim();
      if (!name.isEmpty() && !nodes.containsKey(name)) {
        // 隨機產生不重複且距離足夠的座標
        Point p;
        Random rand = new Random();
        int minDist = 500; // 節點間最小距離
        outer: while (true) {
          int x = rand.nextInt(1000) + 100;
          int y = rand.nextInt(400) + 100;
          p = new Point(x, y);
          for (Point exist : nodes.values()) {
            if (p.distance(exist) < minDist) continue outer;
          }
          break;
        }
        nodes.put(name, p);
        nodeNameField.setText(""); // 新增後清空輸入框
        panel.repaint();
      }
    });

    removeNodeBtn.addActionListener(e -> {
      String name = nodeNameField.getText().trim();
      if (nodes.containsKey(name)) {
        nodes.remove(name);
        // 移除相關的邊
        edges.removeIf(edge -> edge.from.equals(name) || edge.to.equals(name));
        nodeNameField.setText(""); // 刪除後清空輸入框
        panel.repaint();
      }
    });

    gbc.gridx = 2;
    controlPanel.add(addNodeBtn, gbc);
    gbc.gridx = 3;
    controlPanel.add(removeNodeBtn, gbc);

    gbc.gridy = 1; gbc.gridx = 0;
    controlPanel.add(new JLabel("From:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(fromField, gbc);
    gbc.gridx = 2;
    controlPanel.add(new JLabel("To:"), gbc);
    gbc.gridx = 3;
    controlPanel.add(toField, gbc);

    JButton addEdgeBtn = new JButton("Add Edge");
    addEdgeBtn.addActionListener(e -> {
      String from = fromField.getText().trim();
      String to = toField.getText().trim();
      if (nodes.containsKey(from) && nodes.containsKey(to) && !from.equals(to)) {
        Edge newEdge = new Edge(from, to);
        if (!edges.contains(newEdge)) {
          edges.add(newEdge);
          fromField.setText(""); // 新增後清空
          toField.setText("");
          panel.repaint();
        }
      }
    });

    gbc.gridx = 4;
    controlPanel.add(addEdgeBtn, gbc);

    gbc.gridy = 2; gbc.gridx = 0;
    controlPanel.add(new JLabel("Number of Nodes:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(nodeCountField, gbc);
    gbc.gridx = 2;
    controlPanel.add(new JLabel("Number of Edges:"), gbc);
    gbc.gridx = 3;
    controlPanel.add(edgeCountField, gbc);

    JButton autoGenBtn = new JButton("Auto Generate Graph");
    autoGenBtn.addActionListener(e -> autoGenerateGraph());
    gbc.gridx = 4;
    controlPanel.add(autoGenBtn, gbc);

    gbc.gridy = 3; gbc.gridx = 0;
    controlPanel.add(new JLabel("Start Node:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(startNodeField, gbc);

    JButton dfsBtn = new JButton("DFS");
    gbc.gridx = 2; controlPanel.add(dfsBtn, gbc);
    JButton bfsBtn = new JButton("BFS");
    gbc.gridx = 3; controlPanel.add(bfsBtn, gbc);

    // 輸出區塊
    JTextArea outputArea = new JTextArea(6, 18);
    outputArea.setEditable(false);
    outputArea.setLineWrap(true);
    outputArea.setWrapStyleWord(true);
    JScrollPane outputScroll = new JScrollPane(outputArea);
    gbc.gridy = 4; gbc.gridx = 0; gbc.gridwidth = 5;
    gbc.fill = GridBagConstraints.BOTH;
    gbc.weightx = 1.0; gbc.weighty = 1.0;
    controlPanel.add(outputScroll, gbc);
    gbc.gridwidth = 1;
    gbc.weightx = 0; gbc.weighty = 0;
    gbc.fill = GridBagConstraints.HORIZONTAL;

    add(controlPanel, BorderLayout.EAST);

    dfsBtn.addActionListener(e -> {
      String start = startNodeField.getText().trim();
      if (!nodes.containsKey(start)) {
        JOptionPane.showMessageDialog(this, "Start node does not exist.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
      }
      traversalResult = dfs(start, getAdjacencyList());
      outputArea.setText("DFS: " + String.join(" ", traversalResult));
      startAnimation(panel);
    });
    bfsBtn.addActionListener(e -> {
      String start = startNodeField.getText().trim();
      if (!nodes.containsKey(start)) {
        JOptionPane.showMessageDialog(this, "Start node does not exist.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
      }
      traversalResult = bfs(start, getAdjacencyList());
      outputArea.setText("BFS: " + String.join(" ", traversalResult));
      startAnimation(panel);
    });
  }

  // 畫圖區
  class GraphPanel extends JPanel {
    protected void paintComponent(Graphics g) {
      super.paintComponent(g);
      // 畫邊
      for (Edge edge : edges) {
        Point p1 = nodes.get(edge.from);
        Point p2 = nodes.get(edge.to);
        if (p1 != null && p2 != null) {
          g.setColor(Color.BLACK);
          g.drawLine(p1.x, p1.y, p2.x, p2.y);
        }
      }
      // 畫節點，根據 traversalResult 著色動畫
      for (String name : nodes.keySet()) {
        Point p = nodes.get(name);
        int idx = traversalResult.indexOf(name);
        if (!traversalResult.isEmpty() && idx >= 0 && idx < animationIndex) {
          if (idx == 0)
            g.setColor(Color.RED);  // 起點紅
          else
            g.setColor(Color.GREEN); // 其它綠
        } else {
          g.setColor(Color.RED); // 沒被搜尋到也可以用原來顏色
        }
        g.fillOval(p.x - 20, p.y - 20, 40, 40);
        g.setColor(Color.WHITE);
        g.setFont(new Font(Font.SANS_SERIF, Font.BOLD, 18));
        g.drawString(name, p.x - 10, p.y + 7);
      }
    }
  }

  // 記錄邊
  // Edge 類覆寫 equals 與 hashCode 以利判斷重複
  static class Edge {
    String from, to;
    Edge(String f, String t) { from = f; to = t; }
    @Override
    public boolean equals(Object o) {
      if (this == o) return true;
      if (o == null || getClass() != o.getClass()) return false;
      Edge edge = (Edge) o;
      return Objects.equals(from, edge.from) && Objects.equals(to, edge.to);
    }
    @Override
    public int hashCode() {
      return Objects.hash(from, to);
    }
  }

  // autoGenerateGraph 應為 GraphGUI 的成員方法
  private void autoGenerateGraph() {
    int nNodes, nEdges;
    try {
      nNodes = Integer.parseInt(nodeCountField.getText().trim());
      nEdges = Integer.parseInt(edgeCountField.getText().trim());
    } catch (NumberFormatException ex) {
      JOptionPane.showMessageDialog(this, "Please enter valid numbers!", "Error", JOptionPane.ERROR_MESSAGE);
      return;
    }
    if (nNodes < 2 || nEdges < 1) {
      JOptionPane.showMessageDialog(this, "At least 2 nodes and 1 edge required!", "Error", JOptionPane.ERROR_MESSAGE);
      return;
    }
    // 清空現有節點與邊
    nodes.clear();
    edges.clear();

    Random rand = new Random();
    int minDist = 60; // 節點間最小距離
    // 產生節點（自動產生時也避免重複位置且距離足夠）
    for (int i = 0; i < nNodes; ++i) {
      String name = "N" + i;
      Point p;
      outer: while (true) {
        int x = rand.nextInt(450) + 50;
        int y = rand.nextInt(400) + 50;
        p = new Point(x, y);
        for (Point exist : nodes.values()) {
          if (p.distance(exist) < minDist) continue outer;
        }
        break;
      }
      nodes.put(name, p);
    }
    // 產生隨機不重複的邊
    Set<Edge> generatedEdges = new HashSet<>();
    java.util.List<String> names = new ArrayList<>(nodes.keySet());
    while (generatedEdges.size() < nEdges && generatedEdges.size() < nNodes * (nNodes - 1) / 2) {
      String from = names.get(rand.nextInt(names.size()));
      String to = names.get(rand.nextInt(names.size()));
      if (!from.equals(to)) {
        Edge edge = new Edge(from, to);
        if (!generatedEdges.contains(edge)) {
          generatedEdges.add(edge);
        }
      }
    }
    edges.addAll(generatedEdges);

    repaint();
  }

  private Map<String, java.util.List<String>> getAdjacencyList() {
    Map<String, java.util.List<String>> graph = new HashMap<>();
    for (String name : nodes.keySet()) {
        graph.put(name, new ArrayList<>());
    }
    for (Edge edge : edges) {
        // 無向圖兩邊加
        if(graph.containsKey(edge.from) && graph.containsKey(edge.to)) {
            graph.get(edge.from).add(edge.to);
            graph.get(edge.to).add(edge.from);
        }
    }
    return graph;
}

  private java.util.List<String> dfs(String start, Map<String, java.util.List<String>> graph) {
    java.util.List<String> result = new ArrayList<>();
    Set<String> visited = new HashSet<>();
    dfsHelper(start, graph, visited, result);
    return result;
}

private void dfsHelper(String node, Map<String, java.util.List<String>> graph, Set<String> visited, java.util.List<String> result) {
    if (!visited.contains(node)) {
        visited.add(node);
        result.add(node);
        for (String neighbor : graph.get(node)) {
            dfsHelper(neighbor, graph, visited, result);
        }
    }
}

private java.util.List<String> bfs(String start, Map<String, java.util.List<String>> graph) {
    java.util.List<String> result = new ArrayList<>();
    Set<String> visited = new HashSet<>();
    Queue<String> queue = new LinkedList<>();
    queue.add(start);
    visited.add(start);
    while (!queue.isEmpty()) {
        String node = queue.poll();
        result.add(node);
        for (String neighbor : graph.get(node)) {
            if (!visited.contains(neighbor)) {
                queue.add(neighbor);
                visited.add(neighbor);
            }
        }
    }
    return result;
}

  private void startAnimation(GraphPanel panel) {
    animationIndex = 0;
    if (animationTimer != null && animationTimer.isRunning()) {
      animationTimer.stop();
    }
    animationTimer = new javax.swing.Timer(500, new ActionListener() {
      public void actionPerformed(ActionEvent e) {
        animationIndex++;
        if (animationIndex > traversalResult.size()) {
          animationTimer.stop();
        }
        panel.repaint();
      }
    });
    animationTimer.start();
  }

  public static void main(String[] args) {
    SwingUtilities.invokeLater(() -> new GraphGUI().setVisible(true));
  }
}

import javax.swing.*;
import java.awt.*;
import java.awt.event.*;
import java.util.*;

public class GraphGUI extends JFrame {
  private Graph graph = new Graph();
  private JTextField nodeNameField = new JTextField(5);
  private JTextField fromField = new JTextField(3);
  private JTextField toField = new JTextField(3);
  private JTextField nodeCountField = new JTextField(3);
  private JTextField edgeCountField = new JTextField(3);
  private JTextField startNodeField = new JTextField(3);
  private java.util.List<String> traversalResult = new ArrayList<>();
  private int animationIndex = 0;
  private javax.swing.Timer animationTimer;

  public GraphGUI() {
    setTitle("Graph Application");
    setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    setSize(1500, 600);

    GraphPanel panel = new GraphPanel();
    add(panel, BorderLayout.CENTER);

    JPanel controlPanel = new JPanel(new GridBagLayout());
    GridBagConstraints gbc = new GridBagConstraints();
    gbc.insets = new Insets(2, 2, 2, 2);
    gbc.fill = GridBagConstraints.HORIZONTAL;
    gbc.gridy = 0; gbc.gridx = 0;
    controlPanel.add(new JLabel("Node Name:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(nodeNameField, gbc);

    JButton addNodeBtn = new JButton("Add Node");
    JButton removeNodeBtn = new JButton("Remove Node");

    addNodeBtn.addActionListener(e -> {
      String name = nodeNameField.getText().trim();
      if (!name.isEmpty() && !graph.containsNode(name)) {
        Point p;
        Random rand = new Random();
        int minDist = 60;
        outer: while (true) {
          int x = rand.nextInt(1000) + 100;
          int y = rand.nextInt(400) + 100;
          p = new Point(x, y);
          for (Point exist : graph.getNodes().values()) {
            if (p.distance(exist) < minDist) continue outer;
          }
          break;
        }
        graph.addNode(name, p);
        nodeNameField.setText("");
        panel.repaint();
      }
    });

    removeNodeBtn.addActionListener(e -> {
      String name = nodeNameField.getText().trim();
      if (graph.containsNode(name)) {
        graph.removeNode(name);
        nodeNameField.setText("");
        panel.repaint();
      }
    });

    gbc.gridx = 2;
    controlPanel.add(addNodeBtn, gbc);
    gbc.gridx = 3;
    controlPanel.add(removeNodeBtn, gbc);

    gbc.gridy = 1; gbc.gridx = 0;
    controlPanel.add(new JLabel("From:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(fromField, gbc);
    gbc.gridx = 2;
    controlPanel.add(new JLabel("To:"), gbc);
    gbc.gridx = 3;
    controlPanel.add(toField, gbc);

    JButton addEdgeBtn = new JButton("Add Edge");
    addEdgeBtn.addActionListener(e -> {
      String from = fromField.getText().trim();
      String to = toField.getText().trim();
      if (graph.containsNode(from) && graph.containsNode(to) && !from.equals(to)) {
        graph.addEdge(from, to);
        fromField.setText("");
        toField.setText("");
        panel.repaint();
      }
    });

    gbc.gridx = 4;
    controlPanel.add(addEdgeBtn, gbc);

    gbc.gridy = 2; gbc.gridx = 0;
    controlPanel.add(new JLabel("Number of Nodes:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(nodeCountField, gbc);
    gbc.gridx = 2;
    controlPanel.add(new JLabel("Number of Edges:"), gbc);
    gbc.gridx = 3;
    controlPanel.add(edgeCountField, gbc);

    JButton autoGenBtn = new JButton("Auto Generate Graph");
    autoGenBtn.addActionListener(e -> autoGenerateGraph());
    gbc.gridx = 4;
    controlPanel.add(autoGenBtn, gbc);

    gbc.gridy = 3; gbc.gridx = 0;
    controlPanel.add(new JLabel("Start Node:"), gbc);
    gbc.gridx = 1;
    controlPanel.add(startNodeField, gbc);

    JButton dfsBtn = new JButton("DFS");
    gbc.gridx = 2; controlPanel.add(dfsBtn, gbc);
    JButton bfsBtn = new JButton("BFS");
    gbc.gridx = 3; controlPanel.add(bfsBtn, gbc);

    JTextArea outputArea = new JTextArea(6, 18);
    outputArea.setEditable(false);
    outputArea.setLineWrap(true);
    outputArea.setWrapStyleWord(true);
    JScrollPane outputScroll = new JScrollPane(outputArea);
    gbc.gridy = 4; gbc.gridx = 0; gbc.gridwidth = 5;
    gbc.fill = GridBagConstraints.BOTH;
    gbc.weightx = 1.0; gbc.weighty = 1.0;
    controlPanel.add(outputScroll, gbc);
    gbc.gridwidth = 1;
    gbc.weightx = 0; gbc.weighty = 0;
    gbc.fill = GridBagConstraints.HORIZONTAL;

    add(controlPanel, BorderLayout.EAST);

    dfsBtn.addActionListener(e -> {
      String start = startNodeField.getText().trim();
      if (!graph.containsNode(start)) {
        JOptionPane.showMessageDialog(this, "Start node does not exist.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
      }
      traversalResult = graph.dfs(start);
      outputArea.setText("DFS: " + String.join("  ", traversalResult));
      startAnimation(panel);
    });
    bfsBtn.addActionListener(e -> {
      String start = startNodeField.getText().trim();
      if (!graph.containsNode(start)) {
        JOptionPane.showMessageDialog(this, "Start node does not exist.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
      }
      traversalResult = graph.bfs(start);
      outputArea.setText("BFS: " + String.join("  ", traversalResult));
      startAnimation(panel);
    });
  }

  class GraphPanel extends JPanel {
    protected void paintComponent(Graphics g) {
      super.paintComponent(g);
      for (Edge edge : graph.getEdges()) {
        Point p1 = graph.getNodes().get(edge.from);
        Point p2 = graph.getNodes().get(edge.to);
        if (p1 != null && p2 != null) {
          g.setColor(Color.BLACK);
          g.drawLine(p1.x, p1.y, p2.x, p2.y);
        }
      }
      for (String name : graph.getNodes().keySet()) {
        Point p = graph.getNodes().get(name);
        int idx = traversalResult.indexOf(name);
        if (!traversalResult.isEmpty() && idx >= 0 && idx < animationIndex) {
          if (idx == 0)
            g.setColor(Color.RED);
          else
            g.setColor(Color.GREEN);
        } else {
          g.setColor(Color.RED);
        }
        g.fillOval(p.x - 20, p.y - 20, 40, 40);
        g.setColor(Color.WHITE);
        g.setFont(new Font(Font.SANS_SERIF, Font.BOLD, 18));
        g.drawString(name, p.x - 10, p.y + 7);
      }
    }
  }

  private void autoGenerateGraph() {
    int nNodes, nEdges;
    try {
      nNodes = Integer.parseInt(nodeCountField.getText().trim());
      nEdges = Integer.parseInt(edgeCountField.getText().trim());
    } catch (NumberFormatException ex) {
      JOptionPane.showMessageDialog(this, "Please enter valid numbers!", "Error", JOptionPane.ERROR_MESSAGE);
      return;
    }
    if (nNodes < 2 || nEdges < 1) {
      JOptionPane.showMessageDialog(this, "At least 2 nodes and 1 edge required!", "Error", JOptionPane.ERROR_MESSAGE);
      return;
    }
    graph.clear();
    Random rand = new Random();
    int minDist = 60;
    for (int i = 0; i < nNodes; ++i) {
      String name = "N" + i;
      Point p;
      outer: while (true) {
        int x = rand.nextInt(450) + 50;
        int y = rand.nextInt(400) + 50;
        p = new Point(x, y);
        for (Point exist : graph.getNodes().values()) {
          if (p.distance(exist) < minDist) continue outer;
        }
        break;
      }
      graph.addNode(name, p);
    }
    Set<Edge> generatedEdges = new HashSet<>();
    java.util.List<String> names = new ArrayList<>(graph.getNodes().keySet());
    while (generatedEdges.size() < nEdges && generatedEdges.size() < nNodes * (nNodes - 1) / 2) {
      String from = names.get(rand.nextInt(names.size()));
      String to = names.get(rand.nextInt(names.size()));
      if (!from.equals(to)) {
        Edge edge = new Edge(from, to);
        if (!generatedEdges.contains(edge)) {
          generatedEdges.add(edge);
        }
      }
    }
    for (Edge edge : generatedEdges) {
      graph.addEdge(edge.from, edge.to);
    }
    repaint();
  }

  private void startAnimation(GraphPanel panel) {
    animationIndex = 0;
    if (animationTimer != null && animationTimer.isRunning()) {
      animationTimer.stop();
    }
    animationTimer = new javax.swing.Timer(500, new ActionListener() {
      public void actionPerformed(ActionEvent e) {
        animationIndex++;
        if (animationIndex > traversalResult.size()) {
          animationTimer.stop();
        }
        panel.repaint();
      }
    });
    animationTimer.start();
  }

  public static void main(String[] args) {
    SwingUtilities.invokeLater(() -> new GraphGUI().setVisible(true));
  }
}
